<?phprequire_once("../../../../../../app/Mage.php");Mage::app('default');session_start();$captcha = imagecreatefromgif('cross.gif');//set some variables$black = imagecolorallocate($captcha, 0, 0, 0);$white = imagecolorallocate($captcha, 225, 225, 225);$red = imagecolorallocate($captcha, 225, 0, 0);$font = 'arial.ttf';//random stuff$string = md5(microtime() * mktime());$text = substr($string, 0, 5);// $_SESSION['code'] = $text;Mage::getSingleton('core/session')->setCodeCaptcha($text);// setcookie("user", $text, time()+3600);//create some stupid stuff// $_SESSION['favcolor'] = $text;// $_SESSION['animal']   = $text;imagettftext($captcha, 14, 5, 5, 25, $red, $font, $text);// begin to create imageheader('content-type: image/png');imagepng($captcha);//clean upimagedestroy($captcha);?>